import React, { Fragment, useState } from "react";

import { Button, Row, Col } from "react-bootstrap";

import { RecipeCard } from "./RecipeCard";
import { RecipeObject } from "./RecipeObject";

const example = new RecipeObject("nameData", "descriptionData", "https:...", [
  { name: "ingr1", amount: "15 gr" },
  { name: "ingr2", amount: "15 pcs" },
]);
example.setInformation("information Data");

// var recipes = [example, example, example];

export function RecipesList() {

  const [recipes, setRecipes] = useState();

  const addRecipe = (index) => {
    console.log(index);
    console.log(recipes[index]);
    recipes[index].isSaved = true;
  };

  const deleteRecipe = (index) => {
    console.log(index);
  };

  
//   setRecipes(example)
//   setRecipes(...recipes, example)
//   setRecipes(...recipes, example)
//   setRecipes(...recipes, example)
//   setRecipes(...recipes, example)

  return (
    <div>
      <h1 className="text-center" style={{ width: "100%" }}>
        Recipes List
      </h1>
      {recipes != undefined ? recipes.map((recipe, index) => {
        return (
          <Fragment key={index}>
            <Row>
              <Col xs={9} md={9}>
                <RecipeCard recipeData={recipe} />
              </Col>
              <Col xs={3} md={3}>
                {recipe.isSaved ? (
                  <Button
                    style={{ margin: "auto" }}
                    onClick={() => deleteRecipe(index)}
                  >
                    {" "}
                    -{" "}
                  </Button>
                ) : (
                  <Button
                    style={{ margin: "auto" }}
                    onClick={() => addRecipe(index)}
                  >
                    {" "}
                    +{" "}
                  </Button>
                )}
              </Col>
            </Row>
            <br></br>
          </Fragment>
        );
      }):null}
    </div>
  );
}
